import Header, {HEADERLINKS} from '../components/Header';
import { useRef, useEffect } from 'react';



function Skills() {
  return (
    <div>
      <Header active={HEADERLINKS.SKILLS}/>
      <div className="skill-page-wrapper content-container">
        <ul className="skill-page__list">
          {
            SKILLS.map((skill, index) => (
              <SkillItem skill={skill} key={index}/>
            )) 
          }
        </ul>
      </div>  
    </div>
  )
}


const SkillItem = ({skill}) => {
  const skillRef = useRef();
  useEffect(() => {
    skillRef.current.style.width = `${skill.level / 100}%`
  }, [])
  return (
    <li className="skill-page__listitem">
      <p>{skill.name}</p>
      <div className="skill-empty">
        <div className="skill-filled" ref={skillRef}></div>
      </div>
    </li>
  )
}
export default Skills;



const SKILLS = [
  {
    name: "React",
    level: "75",
    logo: ""
  },
  {
    name: "Webpack",
    level: "56",
    logo: ""
  },
  {
    name: "SCSS",
